import 'package:auto_size_text/auto_size_text.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:link_text/link_text.dart';import 'package:url_launcher/url_launcher.dart';import '../../../../help/Colors.dart';import '../../../../help/text_style.dart';class Container_OF_post extends StatelessWidget {  const Container_OF_post({    Key? key, required this.images, required this.text, required this.link,  }) : super(key: key);  final String images ;  final String text ;  final String link;  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.all(8.0),      child: Column(        children: [          if(images != "")            Container(                width: MediaQuery.of(context)                    .size                    .width /                    1.8,                child: ClipRRect(                    borderRadius:                    BorderRadius.circular(10),                    child: Image.asset(                      "${images}",                      fit: BoxFit.fill,                    ))),          if(text != "")            Padding(              padding: const EdgeInsets.all(8.0),              child: Container(                  width: MediaQuery.of(context)                      .size                      .width /                      1.2,                  child: AutoSizeText(                    textAlign: TextAlign.start,                    textDirection: TextDirection.rtl,                    "${text}",                    style: Text_Style.getstyle(                        fontsize:                        MediaQuery.of(context)                            .size                            .height /                            35,                        ColorText:                        Colorsapp.mainColor,                        fontWeight: FontWeight.w800),                  )),            ),          if(link != "")          Padding(            padding:  EdgeInsets.only(left:MediaQuery.of(context).size.width/100, ),            child: Align(              alignment: Alignment.centerLeft,              child: LinkText(                linkStyle: TextStyle(                  color: Colors.blue,                  decoration: TextDecoration.underline,                  fontSize:  MediaQuery.of(context)                      .size                      .height /                      40,                ),                "${link}",                textAlign: TextAlign.center,                shouldTrimParams: false,              ),            ),          ),        ],      ),    );  }}